const price=document.querySelectorAll("#price"),quantity=document.querySelectorAll("#quantity"),total=document.querySelectorAll("#total-price"),subtotal=document.querySelector("#subtotal"),inputSubtotal=document.querySelector("#input-subtotal"),cartSubtotal=document.querySelectorAll("#cart-subtotal");function subTotal(){quantity.forEach(t=>{t.addEventListener("change",()=>{let t=0;for(i=0;i<price.length;i++)total[i].innerText=price[i].value*quantity[i].value,t+=price[i].value*quantity[i].value,cartSubtotal[i].value=price[i].value*quantity[i].value;subtotal.innerText=t,inputSubtotal.value=t})})}subTotal();