const filterBtn=document.querySelectorAll(".filter-btn"),productItem=document.querySelectorAll(".products");productItem?document.querySelector("#product-count").textContent=productItem.length:document.querySelector("#product-count").textContent=0,filterBtn&&filterBtn.forEach(t=>{t.addEventListener("click",t=>{t.preventDefault();const e=t.target.dataset.filter;productItem.forEach(t=>{"All Products"===e?(t.style.display="flex",document.querySelector("#category-text").textContent="All Products",document.querySelector("#product-count").textContent=productItem.length):(t.classList.contains(e)?t.style.display="flex":t.style.display="none",document.querySelector("#category-text").textContent=e,document.querySelector("#product-count").textContent=t.parentElement.getElementsByClassName(e).length)})})});