const header=document.querySelector("#main-header"),burgerBtn=document.querySelector(".burger-btn");let burgerOpen=!1;const navLinks=document.querySelector(".nav-links"),links=document.querySelectorAll(".nav-links li");window.addEventListener("scroll",()=>{10<window.scrollY?header.classList.add("scrolled"):header.classList.remove("scrolled")}),burgerBtn.addEventListener("click",()=>{burgerOpen?(burgerBtn.classList.remove("open"),navLinks.classList.remove("open"),burgerOpen=!1,links.forEach(e=>{e.classList.remove("fade")})):(burgerBtn.classList.add("open"),navLinks.classList.toggle("open"),burgerOpen=!0,links.forEach(e=>{e.classList.toggle("fade")}))});