const header=document.querySelector("#main-header"),burgerBtn=document.querySelector(".burger-btn");let burgerOpen=!1;const navLinks=document.querySelector(".nav-links"),links=document.querySelectorAll(".nav-links li");window.addEventListener("scroll",()=>{10<window.scrollY?header.classList.add("scrolled"):header.classList.remove("scrolled")}),burgerBtn.addEventListener("click",()=>{burgerOpen?(burgerBtn.classList.remove("open"),navLinks.classList.remove("open"),burgerOpen=!1,links.forEach(e=>{e.classList.remove("fade")})):(burgerBtn.classList.add("open"),navLinks.classList.toggle("open"),burgerOpen=!0,links.forEach(e=>{e.classList.toggle("fade")}))});const accountMenu=document.querySelector(".account-menu"),burgerMenu=document.querySelector(".burger-menu"),menu=document.querySelector(".user-menu");if(accountMenu){let e=!1;accountMenu.addEventListener("click",()=>{e=e?(burgerMenu.classList.remove("open"),menu.classList.remove("active"),!1):(burgerMenu.classList.add("open"),menu.classList.toggle("active"),!0)})}