function imageGallery(){const u=document.querySelector(".product-highlight img"),a=document.querySelector("#large-img"),g=document.querySelector("#zoom"),e=document.querySelectorAll(".product-gallery img");a.style.backgroundImage=`url(${u.src})`,e.forEach(e=>{e.addEventListener("click",function(){var e=this.src;u.src=e,a.style.backgroundImage=`url(${e})`})}),g.addEventListener("mousemove",e=>{let t=a.style,o=u.width,r=u.height,c=e.pageX-g.offsetLeft,l=e.pageY-g.offsetTop,i=c/o*100,n=l/r*100;.01*o<c&&(i+=.15*i),.01*r<=l&&(n+=.15*n),t.backgroundPositionX=i-9+"%",t.backgroundPositionY=n-9+"%",t.left=c-50+"px",t.top=l-50+"px"},!1)}imageGallery();